import React from 'react';
import { Layout, Tabs, Typography } from 'antd';
import SchemaBuilder from './components/SchemaBuilder';

const { Header, Content } = Layout;
const { Title } = Typography;

const App: React.FC = () => {
  const [activeKey, setActiveKey] = React.useState('builder');
  const [jsonSchema, setJsonSchema] = React.useState<object>({});

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Header style={{ backgroundColor: '#001529' }}>
        <Title style={{ color: 'white', margin: 0 }} level={3}>
          JSON Schema Builder
        </Title>
      </Header>
      <Content style={{ padding: '24px' }}>
        <Tabs activeKey={activeKey} onChange={setActiveKey} type="card">
          <Tabs.TabPane tab="Builder" key="builder">
            <SchemaBuilder onChange={setJsonSchema} />
          </Tabs.TabPane>
          <Tabs.TabPane tab="JSON" key="json">
            <pre style={{ backgroundColor: '#f5f5f5', padding: '16px', borderRadius: '4px', minHeight: '400px', overflow: 'auto' }}>
              {JSON.stringify(jsonSchema, null, 2)}
            </pre>
          </Tabs.TabPane>
        </Tabs>
      </Content>
    </Layout>
  );
};

export default App;
